<style lang="scss">
.spinner {
  &__container {
    opacity: .5;

    @include strict_size(60px, 60px);

    border: 3px solid #CBCBCB;
    border-radius: 10px;
  }

  &, &__container {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  @include strict_size(40px, 40px);

  border: 3px solid #CBCBCB;
  border-radius: 50%;

  svg {
    @include strict_size(28px, 28px);

    animation: spinner 3s linear infinite;
  }
}

@keyframes spinner {
  100% {
    transform: rotate(360deg);
  }
}
</style>